from falcor import *

def render_graph_R():
    g = RenderGraph('R')
    GBufferRT = createPass('GBufferRT', {'outputSize': IOSize.Default, 'samplePattern': SamplePattern.Center, 'sampleCount': 16, 'useAlphaTest': True, 'adjustShadingNormals': True, 'forceCullMode': False, 'cull': CullMode.CullBack, 'texLOD': TexLODMode.Mip0, 'useTraceRayInline': False, 'useDOF': True})
    g.addPass(GBufferRT, 'GBufferRT')
    ImageLoader = createPass('ImageLoader', {'outputSize': IOSize.Default, 'filename': 'E:/Falcor/Source/RenderPasses/Falcor-playground/Data/FreeBlueNoiseTextures/Data/1024_1024/LDR_RGBA_0.png', 'mips': False, 'srgb': True, 'arrayIndex': 0, 'mipLevel': 0})
    g.addPass(ImageLoader, 'ImageLoader')
    ReSTIRGIPass = createPass('ReSTIRGIPass', {'secondaryRayLaunchProbability': 0.20000000298023224, 'russianRouletteProbability': 0.30000001192092896, 'useImportanceSampling': True, 'useInfiniteBounces': True, 'maxBounce': 3, 'useTemporalResampling': True, 'temporalReservoirSize': 20, 'useSpatialResampling': True, 'spatialReservoirSize': 500, 'spatialResamplingRadius': 150, 'spatialNeighborsCount': 10, 'doVisibilityTestEachSamples': False, 'evalDirectLighting': True, 'showVisibilityPointLi': False, 'splitView': False})
    g.addPass(ReSTIRGIPass, 'ReSTIRGIPass')
    ReSTIRDIPass = createPass('ReSTIRDIPass', {'temporalReuseMaxM': 20, 'risSampleNums': 8, 'autoSetMaxM': True, 'useReSTIR': True, 'useTemporalReuse': True, 'useSpatialReuse': True, 'spatialRadius': 5, 'spatialNeighbors': 4})
    g.addPass(ReSTIRDIPass, 'ReSTIRDIPass')
    NRD = createPass('NRD', {'enabled': True, 'method': NRDMethod.RelaxDiffuseSpecular, 'outputSize': IOSize.Default, 'worldSpaceMotion': True, 'disocclusionThreshold': 2.0, 'maxIntensity': 1000.0, 'diffusePrepassBlurRadius': 16.0, 'specularPrepassBlurRadius': 16.0, 'diffuseMaxAccumulatedFrameNum': 31, 'specularMaxAccumulatedFrameNum': 31, 'diffuseMaxFastAccumulatedFrameNum': 2, 'specularMaxFastAccumulatedFrameNum': 2, 'diffusePhiLuminance': 2.0, 'specularPhiLuminance': 1.0, 'diffuseLobeAngleFraction': 0.800000011920929, 'specularLobeAngleFraction': 0.8999999761581421, 'roughnessFraction': 0.5, 'diffuseHistoryRejectionNormalThreshold': 0.0, 'specularVarianceBoost': 1.0, 'specularLobeAngleSlack': 10.0, 'disocclusionFixEdgeStoppingNormalPower': 8.0, 'disocclusionFixMaxRadius': 32.0, 'disocclusionFixNumFramesToFix': 4, 'historyClampingColorBoxSigmaScale': 2.0, 'spatialVarianceEstimationHistoryThreshold': 4, 'atrousIterationNum': 6, 'minLuminanceWeight': 0.0, 'depthThreshold': 0.019999999552965164, 'luminanceEdgeStoppingRelaxation': 0.5, 'normalEdgeStoppingRelaxation': 0.30000001192092896, 'roughnessEdgeStoppingRelaxation': 0.30000001192092896, 'enableAntiFirefly': False, 'enableReprojectionTestSkippingWithoutMotion': False, 'enableSpecularVirtualHistoryClamping': False, 'enableRoughnessEdgeStopping': True, 'enableMaterialTestForDiffuse': False, 'enableMaterialTestForSpecular': False})
    g.addPass(NRD, 'NRD')
